using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using ValidatingFileUploadExtension;
using ValidatingFileUploadExtension.Controllers;
using Xunit;

namespace Tests
{
    public class UploadControllerUnitTest
    {
        private readonly UploadController _controller = new UploadController();

        [Fact]
        public void WhenUploadFakeExtensionFile_ThenReturnBadRequestResultWithAcceptableFalseAndFakeStatus()
        {
            var file = GenerateTextFormFile("invalid.jpg");

            var result = _controller.Upload(file);

            var badRequestResult = Assert.IsType<BadRequestObjectResult>(result);
            var recordResult = Assert.IsType<Result>(badRequestResult.Value);
            Assert.False(recordResult.Acceptable);
            Assert.Equal(Status.FAKE, recordResult.Status);
        }

        [Fact]
        public void WhenUploadTrueImageFile_ThenReturnOkResultWithAcceptableTrueAndGenuineStatus()
        {
            var file = GenerateValidJpgFile("valid.jpg");

            var result = _controller.Upload(file);

            var okResult = Assert.IsType<OkObjectResult>(result);
            var recordResult = Assert.IsType<Result>(okResult.Value);
            Assert.True(recordResult.Acceptable);
            Assert.Equal(Status.GENUINE, recordResult.Status);
        }

        [Fact]
        public void WhenUploadTruePdfFile_ThenReturnOkResultWithAcceptableTrueAndGenuineStatus()
        {
            var file = GenerateValidPdfFile("valid.pdf");

            var result = _controller.Upload(file);

            var okResult = Assert.IsType<OkObjectResult>(result);
            var recordResult = Assert.IsType<Result>(okResult.Value);
            Assert.True(recordResult.Acceptable);
            Assert.Equal(Status.GENUINE, recordResult.Status);
        }

        [Fact]
        public void WhenUploadUnsupportedFile_ThenReturnBadRequestResultWithAcceptableFalseAndNotSupportedStatus()
        {
            var file = GenerateTextFormFile("text.txt");

            var result = _controller.Upload(file);

            var badRequestResult = Assert.IsType<BadRequestObjectResult>(result);
            var recordResult = Assert.IsType<Result>(badRequestResult.Value);
            Assert.False(recordResult.Acceptable);
            Assert.Equal(Status.NOT_SUPPORTED, recordResult.Status);
        }

        private static IFormFile GenerateTextFormFile(string fileName)
        {
            var stream = new MemoryStream();
            var writer = new StreamWriter(stream);
            writer.Write("Text content");
            writer.Flush();
            stream.Position = 0;

            return new FormFile(stream, 0, stream.Length, "file", fileName);
        }

        private static IFormFile GenerateValidJpgFile(string fileName)
        {
            string base64JpgFile = "";
            byte[] imageData = Convert.FromBase64String(base64JpgFile);
            var stream = new MemoryStream(imageData);

            return new FormFile(stream, 0, stream.Length, "file", fileName);
        }

        private static IFormFile GenerateValidPdfFile(string fileName)
        {
            string base64PdfFile = "";
            byte[] pdfData = Convert.FromBase64String(base64PdfFile);
            var stream = new MemoryStream(pdfData);

            return new FormFile(stream, 0, stream.Length, "file", fileName);
        }
    }
}